import "./widget.scss"
import PeopleOutlinedIcon from '@mui/icons-material/PeopleOutlined';
import VibrationOutlinedIcon from '@mui/icons-material/VibrationOutlined';
import ForwardToInboxOutlinedIcon from '@mui/icons-material/ForwardToInboxOutlined';
import NotificationsActiveOutlinedIcon from '@mui/icons-material/NotificationsActiveOutlined';

const Widget = ({ type, quantity }) => {
    let data;
    switch (type) {
        case "user":
            data = {
                title: "USERS",
                icon: <PeopleOutlinedIcon className="icon" />,
            };
            break;
            case "smsNotifications":
            data = {
                title: "SMS",
                icon: <VibrationOutlinedIcon className="icon" />,
            };
            break;
            case "emailNotifications":
            data = {
                title: "EMAIL",
                icon: <ForwardToInboxOutlinedIcon className="icon" />,
            };
            break;
            case "pushNotifications":
            data = {
                title: "PUSH NOTIFICATIONS",
                icon: <NotificationsActiveOutlinedIcon className="icon" />,
            };
            break;
        default:
            break;
    }

    return (
        <div className="widget">
            <div className="left">
                <span className="title">{ data.title }</span>
                <span className="counter">{quantity}</span>
                <span className="link">See details</span>
            </div>
            <div className="right">
                { data.icon }
            </div>
        </div>
    )
}

export default Widget
