import React, { useState, useEffect } from "react";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import AutorenewOutlinedIcon from "@mui/icons-material/AutorenewOutlined";
import TablePagination from '@mui/material/TablePagination';

interface RowData {
  channel: string;
  userEmail: string;
}

const PaginatedTable: React.FC = () => {
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [data, setData] = useState<RowData[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchData = async (page: number, pageSize: number): Promise<RowData[]> => {
    setLoading(true);
    try {
      const response = await fetch(`/api/messages/by-notification?notificationId=1&page=${page}&pageSize=${pageSize}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const responseData = await response.json();
      setError(null);
      return responseData;
    } catch (error) {
      setError("Error fetching data");
      return [];
    } finally {
      setLoading(false);
    }
  };

  const handlePageChange = (event: React.MouseEvent<HTMLButtonElement> | null, newPage: number) => {
    setPage(newPage);
  };

  const handleRowsPerPageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleRefresh = async () => {
    const newData = await fetchData(page, rowsPerPage);
    setData(newData);
  };

  useEffect(() => {
    handleRefresh();
  }, [page, rowsPerPage]);

  return (
    <div className="listContainer">
      <h1>Latest broadcasts</h1>
      <div className="row last-refresh-container">
        <div className="col-md-12 text-end">
          <AutorenewOutlinedIcon className="refresh-icon" onClick={handleRefresh} />
        </div>
      </div>
      <TableContainer component={Paper} className="my-table">
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Channel</TableCell>
              <TableCell>User Email</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((row, index) => (
              <TableRow key={index}>
                <TableCell>{row.channel}</TableCell>
                <TableCell>{row.userEmail}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={10} // Total number of items, replace with actual count
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handlePageChange}
          onRowsPerPageChange={handleRowsPerPageChange}
        />
      </TableContainer>
    </div>
  );
}

export default PaginatedTable;
