import { useState } from "react";
import Navbar from "../../components/navbar/Navbar"
import Sidebar from "../../components/sidebar/Sidebar"
import List from "../../components/list/List"
import "./new-broadcast.scss"
import axios from "axios";
import { Button } from "@mui/material";

const NewBroadcast = () => {
  const [selectedTopic, setSelectedTopic] = useState("");
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [notifications, setNotifications] = useState([]);

  const handleTopicChange = (event) => {
    setSelectedTopic(event.target.value);
  }

  const handleMessageChagne = (event) => {
    setMessage(event.target.value);
  }

  const handleSubmit = async (event) => {
    event.preventDefault();

    console.log("Selected topic: ", selectedTopic);
    console.log("Message: ", message);

    setIsLoading(true);

    const broadcastData = {
      category: selectedTopic,
      message: message,
    };

    try {
      const response = await axios.post(
        "http://localhost:8080/api/notifications/send",
        broadcastData
      );
      console.log("Broadcast sent:", response.data);
      setSelectedTopic("");
      setMessage("");
    } catch (error) {
      console.error("Error sending broadcast:", error);
    }
    setIsLoading(false);
  };

  return (
    <div className="newBroadcast">
        <Sidebar />
        <div className="newBroadcastContainer">
            <Navbar />
            <div className="top">
              <h1>Send a new broadcast</h1>
              <form className="broadcast-form" onSubmit={handleSubmit}>
    <div className="row">
      <div className="col-md-6">
        <label htmlFor="topic">Topic:</label>
        <select id="topic" className="form-select" value={selectedTopic} onChange={handleTopicChange}>
          <option value="">Select a topic</option>
          <option value="SPORTS">SPORTS</option>
          <option value="FINANCE">FINANCE</option>
          <option value="FILMS">FILMS</option>
        </select>
      </div>
      <div className="col-md-6">
        <label htmlFor="message">Message:</label>
        <textarea
          id="message"
          className="form-control"
          value={message}
          onChange={handleMessageChagne}
          rows="1"
        />
      </div>
    </div>
    <div className="row mt-3">
      <div className="col-md-6">
        <button type="submit" className="btn btn-primary" disabled={isLoading}>
          {isLoading ? "Sending..." : "SEND"}
        </button>
      </div>
    </div>
  </form>
            </div>




            <div className="top">
              <form className="broadcast-form" onSubmit={handleSubmit}>
                <label htmlFor="topic">Topic:</label>
                <select id="topic" value={selectedTopic} onChange={handleTopicChange}>
                  <option value="">Select a topic</option>
                  <option value="SPORTS">SPORTS</option>
                  <option value="FINANCE">FINANCE</option>
                  <option value="FILMS">FILMSx</option>
                </select>

                <label htmlFor="message">Messge:</label>
                <textarea
                  id="message"
                  value={message}
                  onChange={handleMessageChagne}
                  rows="4"
                  cols="50"
                />
                <button type="submit">SEND</button>
              </form>
            </div>
            <div className="top">
              <h1>Latestss broadcasts</h1>
              <List notifications={notifications} setNotifications={setNotifications} />
            </div>
        </div>
    </div>
  )
}

export default NewBroadcast
