import React, { useState, useEffect } from "react";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import AutorenewOutlinedIcon from "@mui/icons-material/AutorenewOutlined";
import TablePagination from '@mui/material/TablePagination';

interface RowData {
  channel: string;
  userEmail: string;
  userId: number;
  sentTime: string
}

const PaginatedTable: React.FC = () => {
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [data, setData] = useState<RowData[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [totalCount, setTotalCount] = useState(0);

  const fetchData = async (page: number, pageSize: number): Promise<RowData[]> => {
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:8080/api/messages/by-notification?notificationId=1&page=${page}&pageSize=${pageSize}`);
      console.log('response is ----->', response);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const responseData = await response.json();
      setError(null);
      return responseData;
    } catch (error) {
      setError("Error fetching data");
      return [];
    } finally {
      setLoading(false);
    }
  };

  const handlePageChange = (event: React.MouseEvent<HTMLButtonElement> | null, newPage: number) => {
    setPage(newPage);
  };

  const handleRowsPerPageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleRefresh = async () => {
    const response: any = await fetchData(page + 1, rowsPerPage);
    setData(response.messages);
    setTotalCount(response.totalCount);
  };

  useEffect(() => {
    //handleRefresh();
  }, [page, rowsPerPage]);

  return (
    <div className="listContainer">
      <h1>Latest broadcastss</h1>
      <div className="row last-refresh-container">
        <div className="col-md-2 text-end">
          <select id="fruitSelect" className="form-select">
            <option value="apple">ID: 1, MESSAGE: foobarbaz foobarbaz foobarbaz</option>
            <option value="apple">ID: 2, MESSAGE: rbaz rbaz rbazfoobarbaz</option>
            <option value="apple">ID: 3, MESSAGE: foobarbazrbazrbazrbazrbaz</option>
          </select>
        </div>
      </div>
      <TableContainer component={Paper} className="my-table">
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
            <TableCell>Sent time</TableCell>
              <TableCell>Channel</TableCell>
              <TableCell>User Id</TableCell>
              <TableCell>User Email</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((row, index) => (
              <TableRow key={index}>
                <TableCell>{row.sentTime}</TableCell>
                <TableCell>{row.channel}</TableCell>
                <TableCell>{row.userId}</TableCell>
                <TableCell>{row.userEmail}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[10]}
          component="div"
          count={totalCount} // Total number of items, replace with actual count
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handlePageChange}
          onRowsPerPageChange={handleRowsPerPageChange}
        />
      </TableContainer>
    </div>
  );
}

export default PaginatedTable;
