import { useState } from "react";
import Navbar from "../../components/navbar/Navbar"
import Sidebar from "../../components/sidebar/Sidebar"
import List from "../../components/list/List"
import "./new-broadcast.scss"
import axios from "axios";
import { Button } from "@mui/material";

const NewBroadcast = () => {
  const [selectedTopic, setSelectedTopic] = useState("SPORTS");
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [notifications, setNotifications] = useState([]);

  const handleTopicChange = (event) => {
    setSelectedTopic(event.target.value);
  }

  const handleMessageChange = (event) => {
    const newMessage = event.target.value;
    if (newMessage.length <= 150) {
      setMessage(newMessage);
    }
  }

  const handleSubmit = async (event) => {
    event.preventDefault();

    console.log("Selected topic: ", selectedTopic);
    console.log("Message: ", message);

    setIsLoading(true);

    const broadcastData = {
      category: selectedTopic,
      message: message,
    };

    const handleBroadcast = () => {
      fetchNotifications();
    };

    try {
      const response = await axios.post(
        "http://localhost:8080/api/notifications/send",
        broadcastData
      );
      console.log("Broadcast sent:", response.data);
      setSelectedTopic("");
      setMessage("");
    } catch (error) {
      console.error("Error sending broadcast:", error);
    }
    setIsLoading(false);
  };

  return (
    <div className="newBroadcast">
      <Sidebar />
      <div className="newBroadcastContainer">
        <Navbar />
        <div className="top">
          <h1>Send a new broadcast</h1>
          <form className="broadcast-form" onSubmit={handleSubmit}>
            <div className="row align-items-end">
              <div className="col-md-2">
                <label htmlFor="topic" className="labelForm">Topic:</label>
                <select id="topic" className="form-select" value={selectedTopic} onChange={handleTopicChange}>
                  <option value="SPORTS">SPORTS</option>
                  <option value="FINANCE">FINANCE</option>
                  <option value="FILMS">FILMS</option>
                </select>
              </div>
              <div className="col-md-8">
                <label htmlFor="message" className="labelForm">Message:</label>
                <textarea
                  id="message"
                  className="form-control"
                  value={message}
                  onChange={handleMessageChange}
                  rows="1"
                  maxLength="150"
                />
              </div>
              <div className="col-md-2">
                <button type="submit" className="btn btn-dark" style={{ width: "100%" }} disabled={isLoading || message.trim().length === 0}>
                  {isLoading ? "Sending..." : "SEND"}
                </button>
              </div>
            </div>
          </form>
        </div>
        <div className="top">
          <h1>Latest broadcasts</h1>
          <List notifications={notifications} setNotifications={setNotifications} onBroadcast={handleBroadcast}/>
        </div>
      </div>
    </div>
  )
}

export default NewBroadcast;
