import { useState } from "react";
import List from "../../components/list/List"
import "./new-broadcast.scss"
import axios from "axios";
import NewBroadcastForm from "../../components/new-broadcast-form/NewBroadcastForm";

const NewBroadcast = () => {
  const [selectedTopic, setSelectedTopic] = useState("SPORTS");
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [lastRefreshed, setLastRefreshed] = useState(null);

  const handleTopicChange = (event) => {
    setSelectedTopic(event.target.value);
  }

  const handleMessageChange = (event) => {
    const newMessage = event.target.value;
    if (newMessage.length <= 150) {
      setMessage(newMessage);
    }
  }

  const handleSubmit = async (event) => {
    event.preventDefault();

    console.log("Selected topic: ", selectedTopic);
    console.log("Message: ", message);

    setIsLoading(true);

    const broadcastData = {
      category: selectedTopic,
      message: message,
    };

    try {
      const response = await axios.post(
        "http://localhost:8080/api/notifications/send",
        broadcastData
      );
      console.log("Broadcast sent:", response.data);
      setSelectedTopic("SPORTS");
      setMessage("");
    } catch (error) {
      console.error("Error sending broadcast:", error);
    }
    setIsLoading(false);
    handleBroadcast();
  };

  const handleBroadcast = () => {
    console.log('so it was runned?');
    fetchNotifications();
  };

  const fetchNotifications = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/notifications/last');
      setNotifications(response.data);
      //setLastRefreshed(new Date());
    } catch (error) {
      console.error('Error fetching notifications:', error);
    }
  };

