import "./widget.scss"
import PeopleOutlinedIcon from '@mui/icons-material/PeopleOutlined';

const Widget = ({ type }) => {
    let data;
    switch (type) {
        case "user":
            data = {
                title: "USERS",
                icon: <PeopleOutlinedIcon className="icon" />,
            };
            break;
            case "smsNotifications":
            data = {
                title: "SMS",
                icon: <PeopleOutlinedIcon className="icon" />,
            };
            break;
            case "emailNotifications":
            data = {
                title: "EMAIL",
                linkText: "See all users",
                icon: <PeopleOutlinedIcon className="icon" />,
            };
            break;
            case "pushNotifications":
            data = {
                title: "PUSH NOTIFICATIONS",
                icon: <PeopleOutlinedIcon className="icon" />,
            };
            break;
        default:
            break;
    }

    return (
        <div className="widget">
            <div className="left">
                <span className="title">USERS</span>
                <span className="counter">21343</span>
                <span className="link">See details</span>
            </div>
            <div className="right">
                <PeopleOutlinedIcon className="icon" />
            </div>
        </div>
    )
}

export default Widget
